package com.jwzt.common.utils;import java.util.Random;/** *  * @author ysf * */public class CommonUtils {	/**	 * 获取指定长度的随机字符串	 * 	 * @param length	 * @return	 */	public static String getRandomString(int length) {		String base = "abcdefghijklmnopqrstuvwxyz0123456789";		Random random = new Random();		StringBuffer sb = new StringBuffer();		for (int i = 0; i < length; i++) {			int number = random.nextInt(base.length());			sb.append(base.charAt(number));		}		return sb.toString();	}	/**	 * 随机指定范围内N个不重复的数 最简单最基本的方法	 * 	 * @param min	 *            指定范围最小值	 * @param max	 *            指定范围最大值	 * @param n	 *            随机数个数	 */	public static Integer[] getRandomArray(int min, int max, int n) {		if (n >= (max - min + 1) || max < min) {			n = max - min;		}		Integer[] result = new Integer[n];		Integer count = 0;		while (count < n) {			Integer num = (int) (Math.random() * (max - min)) + min;			boolean flag = true;			for (Integer j = 0; j < n; j++) {				if (num == result[j]) {					flag = false;					break;				}			}			if (flag) {				result[count] = num;				count++;			}		}		return result;	}}